mapper.select:

select   A.pernm  , b.divinm,  c.teltime,  c.telxcoordi, c.telycodrdi, a.handphone as handphone
from tb_ja001 a with (nolock),
tb_mcoordinate c with (nolock)   ,
tb_jc002 b with (nolock)
where a.divicd = b.divicd
and a.rtclafi ='001'
and c.inputdate = #{frdate}
and a.handphone=c.telnumber
and c.telnumber= #{handphone}
order by  c.teltime

mapper.update:
update tb_e401
set indate = #{indate}
,
recedate=#{recedate}
,
recetime=#{recetime}
,
hitchdate=#{hitchdate}
,
hitchhour=#{hitchhour}
,
actcd=#{actcd}
,
equpcd=#{equpcd}
,
contcd=#{contcd}
,
contents=#{contents}
,
remark=#{remark}
,
perid=#{perid}
,
reperid=#{reperid}
where custcd = #{custcd} and spjangcd = #{spjangcd} and recenum = #{recenum} and recedate = #{recedate}

update tb_callmain
set regdate = #{regdate}
,
calldate=#{calldate}
,
calltime=#{calltime}
,
callnm=#{callnm}
,
callnum=#{callnum}
,
callmemo=#{callmemo}
,
callbackflag=#{callbackflag}
,
callbacktime=#{callbacktime}
,
callbackmemo=#{callbackmemo}
,
callendmemo=#{callendmemo}
where seq = #{seq}

update TB_E601CALL
set regdate = #{regdate}
,
tel=#{tel}
,
actmail=#{actmail}
,
remark=#{remark}
where actcd = #{actcd} and seq = #{seq}

