mapper.select:
select spernm, smemo
from tb_msmanual WITH (NOLOCK)
Where 1 = 1 and subkey = #{sseq}

select count(*) as count
from tb_msmanual WITH (NOLOCK)
Where 1 = 1 and sseq = #{value}

mapper.update:
update tb_msmanual set
sgourpcd = #{sgourpcd}
,
ssubject=#{ssubject}
,
smemo=#{smemo}
,
sinputdate=#{sinputdate}
,
spernm=#{spernm}
,
sflag=#{sflag}
where sseq = #{sseq}
